import React from "react";
import "./Pagination.css";

export default function Pagination(props) {
  const { pageIndex, total, perPage, onNext, onPrevious } = props;
  const lastPage = Math.ceil(total / perPage);
  const previous =
    pageIndex > 1 ? <button onClick={onPrevious} className="Pagination-paginate">Previous</button> : null;
  const next =
    pageIndex < lastPage ? <button onClick={onNext} className="Pagination-paginate">Next</button> : null;
  return (
    <div className="Pagination">
      <div className="Pagination-actions">
        {previous}
        {next}
      </div>
      <div className="Pagination-stats">
        Page {pageIndex} of {lastPage}
        &nbsp;(displaying {perPage} items per page)
      </div>
    </div>
  );
}
